sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	try {
		if (isLoading || newValue === '') {
			return;
		}
		if (newValue !== '') {
			g_form.setSectionDisplay("device_details", true);
			g_form.setSectionDisplay("delivery_details", true);
			g_form.getReference('device_name', getDetails);
		}
	}
	catch (error) {
		alert("The Error is: " + error.message);
	}
}
function getDetails(details) {
	g_form.setValue('u_model', details.model_number);
	g_form.setValue('u_device_description',details.short_description);
	g_form.setValue('u_price',details.u_price_1);
	g_form.setValue('u_expiration_date',details.warranty_expiration);
}
